const response = $input.first().json;
const message = response.choices[0].message;

let prevData;
try {
  prevData = $("Merge Query Results").first().json;
} catch (error) {
  prevData = $('Initialize Data').first().json;
}

const conversation = prevData.query_conversation || [];

const assistantMsg = {
  role: 'assistant',
  content: message.content || null,
  tool_calls: message.tool_calls || []
};

if (assistantMsg.tool_calls && assistantMsg.tool_calls.length > 0) {
  assistantMsg.content = null;
}

conversation.push(assistantMsg);

const toolCallsCount = (message.tool_calls || []).length;
console.log(`Parse Query Response - Iteration ${prevData.iteration + 1}: Received ${toolCallsCount} tool calls`);

if (toolCallsCount === 0) {
  console.log('No tool calls - moving to Analysis Phase');
}

return {
  json: {
    ...prevData,
    query_conversation: conversation,
    has_tools: !!(message.tool_calls && message.tool_calls.length > 0),
    tool_calls: message.tool_calls || [],
    iteration: (prevData.iteration || 0) + 1,
    query_complete: !(message.tool_calls && message.tool_calls.length > 0)
  }
};

const tools = $input.first().json.tool_calls || [];
const data = $input.first().json;

if (!Array.isArray(tools) || tools.length === 0) {
  throw new Error('No tool_calls found in response');
}

console.log(`Split Query Tools - Processing ${tools.length} tool calls in parallel`);

return tools.map((t, index) => {
  if (!t.id) {
    throw new Error(`Tool call ${index} missing id: ${JSON.stringify(t)}`);
  }
  if (!t.function || !t.function.name) {
    throw new Error(`Tool call ${index} missing function name: ${JSON.stringify(t)}`);
  }

  const toolArgs = typeof t.function.arguments === 'string' ? JSON.parse(t.function.arguments) : (t.function.arguments || {});

  console.log(`  - Tool ${index + 1}: ${t.function.name} (ID: ${t.id})`);

  return {
    json: {
      ...data,
      tool_id: t.id,
      tool_name: t.function.name,
      tool_args: toolArgs,
      tool_index: index
    }
  };
});

const jiraResponse = $input.item.json;
const executionTimestamp = new Date().toISOString();

const splitToolsItems = $("Split Query Tools").all();
const allInputItems = $input.all();
const currentIndex = allInputItems.findIndex(item => JSON.stringify(item.json) === JSON.stringify(jiraResponse));

let originalData;
let matchMethod = 'unknown';

// Try index matching first
if (currentIndex >= 0 && currentIndex < splitToolsItems.length) {
  originalData = splitToolsItems[currentIndex].json;
  matchMethod = 'index';
} else {
  // Fallback to issue key matching
  const jiraKey = jiraResponse.key || jiraResponse.issues?.[0]?.key;
  if (jiraKey) {
    const matched = splitToolsItems.find(item => {
      const issueKey = item.json.tool_args?.issue_key;
      return issueKey && jiraKey && jiraKey.includes(issueKey);
    });
    if (matched) {
      originalData = matched.json;
      matchMethod = 'issue_key';
    }
  }

  // Last resort: use tool_index if available
  if (!originalData) {
    const toolIndex = jiraResponse.tool_index;
    if (typeof toolIndex === 'number' && toolIndex < splitToolsItems.length) {
      originalData = splitToolsItems[toolIndex].json;
      matchMethod = 'tool_index';
    } else {
      originalData = splitToolsItems[0]?.json;
      matchMethod = 'fallback_first';
    }
  }
}

const toolId = originalData?.tool_id;
const toolName = originalData?.tool_name;
const toolArgs = originalData?.tool_args;

if (!toolId) {
  throw new Error(`Format Query Results - Missing tool_id. Match method: ${matchMethod}, Current index: ${currentIndex}, Total split items: ${splitToolsItems.length}`);
}

console.log(`Format Query Results - Tool: ${toolName}, ID: ${toolId}, Match: ${matchMethod}`);

const toolResultMap = {
  'search_jira': () => ({
    found: jiraResponse.issues ? jiraResponse.issues.length : 0,
    issues: jiraResponse.issues || []
  }),
  'get_jira_issue': () => ({
    key: jiraResponse.key,
    summary: jiraResponse.fields?.summary,
    description: jiraResponse.fields?.description,
    status: jiraResponse.fields?.status?.name,
    fields: jiraResponse.fields
  }),
  'get_jira_comments': () => ({
    total: jiraResponse.total || (Array.isArray(jiraResponse) ? jiraResponse.length : 0),
    comments: Array.isArray(jiraResponse) ? jiraResponse : (jiraResponse.comments || [])
  }),
  'get_jira_changelog': () => ({
    total: jiraResponse.changelog?.histories?.length || 0,
    histories: jiraResponse.changelog?.histories || []
  }),
  'get_jira_attachments': () => ({
    attachments: jiraResponse.fields?.attachment || jiraResponse.attachment || []
  })
};

const result = toolResultMap[toolName] ? toolResultMap[toolName]() : jiraResponse;

return {
  json: {
    tool_id: toolId,
    tool_name: toolName,
    tool_args: toolArgs,
    result: result,
    _meta: {
      match_method: matchMethod,
      execution_timestamp: executionTimestamp
    }
  }
};

const items = $input.all();
const baseData = $('Parse Query Response').first().json;
const conv = baseData.query_conversation;

console.log(`Merge Query Results - Merging ${items.length} tool results into conversation`);

items.forEach((item, index) => {
  const toolResult = {
    role: 'tool',
    tool_call_id: item.json.tool_id,
    content: typeof item.json.result === 'string' ? item.json.result : JSON.stringify(item.json.result)
  };

  if (!toolResult.tool_call_id) {
    throw new Error(`Merge Query Results - Missing tool_call_id for tool ${index + 1}: ${item.json.tool_name}`);
  }

  console.log(`  - Added result for ${item.json.tool_name} (ID: ${toolResult.tool_call_id})`);
  conv.push(toolResult);
});

console.log(`Conversation now has ${conv.length} messages`);

return { json: { ...baseData, query_conversation: conv } };

const queryData = $input.first().json;
const ticketData = $('Initialize Data').first().json;

console.log(`Prepare Analysis Data - Transitioning to Analysis Phase after ${queryData.iteration} iterations`);

const analysisConversation = [
  {
    role: 'user',
    content: `Based on the following ticket and all the gathered query results, analyze and create a comprehensive summary:

**Original Ticket:**
- Key: ${ticketData.ticket_key}
- Summary: ${ticketData.summary}
- Description: ${ticketData.description}
- Project: ${ticketData.project}
- Issue Type: ${ticketData.issue_type}
- Status: ${ticketData.status}
- Priority: ${ticketData.priority}
- Assignee: ${ticketData.assignee}
- Reporter: ${ticketData.reporter}
- Created: ${ticketData.created}
- Updated: ${ticketData.updated}

**Gathered Data:**
The conversation below contains all Jira queries performed and their results:

${JSON.stringify(queryData.query_conversation, null, 2)}

**YOUR TASK (Analysis Phase):**
Analyze all the gathered information and create a comprehensive summary that includes:

1. **Ticket Overview**: Brief summary of the ticket
2. **Similar Tickets Found**: List similar/related tickets with links
3. **Solutions Extracted**: Key solutions and fixes found from similar ticket comments
4. **Resolution Patterns**: Patterns identified from changelogs of resolved tickets
5. **Key Insights**: Important insights and recommendations
6. **Suggested Next Steps**: Actionable next steps for resolving this ticket

Format the summary clearly with sections and use markdown. Do NOT use any tools - this is pure analysis based on the data already gathered.

The final summary should be ready to post as a comment on ticket ${ticketData.ticket_key}.`
  }
];

return {
  json: {
    ...ticketData,
    ...queryData,
    analysis_conversation: analysisConversation,
    phase: 'analysis'
  }
};

const response = $input.first().json;
const message = response.choices[0].message;
const prevData = $('Prepare Analysis Data').first().json;

console.log('Parse Analysis - Analysis complete, summary generated');

return {
  json: {
    ...prevData,
    final_summary: message.content,
    analysis_complete: true
  }
};

const data = $input.first().json;

console.log(`Prep Comment - Preparing to post summary to ticket ${data.ticket_key}`);

return {
  json: {
    ticket_key: data.ticket_key,
    jira_url: data.jira_url,
    comment: data.final_summary
  }
};

const items = $input.all();

console.log(`=== Which Query Tool (Code Router) ===`);
console.log(`Total items received: ${items.length}`);

// Initialize output arrays (5 outputs matching 5 tool types)
const search_jira = [];
const get_jira_issue = [];
const get_jira_comments = [];
const get_jira_changelog = [];
const get_jira_attachments = [];

// Route each item to correct output based on tool_name
items.forEach((item, index) => {
  const toolName = item.json.tool_name;
  console.log(`Item ${index}: tool_name="${toolName}", tool_id="${item.json.tool_id}"`);

  switch(toolName) {
    case 'search_jira':
      search_jira.push(item);
      console.log(`  → Routed to Output 0 (search_jira)`);
      break;
    case 'get_jira_issue':
      get_jira_issue.push(item);
      console.log(`  → Routed to Output 1 (get_jira_issue)`);
      break;
    case 'get_jira_comments':
      get_jira_comments.push(item);
      console.log(`  → Routed to Output 2 (get_jira_comments)`);
      break;
    case 'get_jira_changelog':
      get_jira_changelog.push(item);
      console.log(`  → Routed to Output 3 (get_jira_changelog)`);
      break;
    case 'get_jira_attachments':
      get_jira_attachments.push(item);
      console.log(`  → Routed to Output 4 (get_jira_attachments)`);
      break;
    default:
      console.error(`  ✗ ERROR: Unknown tool_name: "${toolName}"`);
  }
});

console.log(`\nOutput Summary:`);
console.log(`  Output 0 (search_jira): ${search_jira.length} items`);
console.log(`  Output 1 (get_jira_issue): ${get_jira_issue.length} items`);
console.log(`  Output 2 (get_jira_comments): ${get_jira_comments.length} items`);
console.log(`  Output 3 (get_jira_changelog): ${get_jira_changelog.length} items`);
console.log(`  Output 4 (get_jira_attachments): ${get_jira_attachments.length} items`);

// Return 5 outputs (n8n expects array of arrays for multiple outputs)
return [
  search_jira,
  get_jira_issue,
  get_jira_comments,
  get_jira_changelog,
  get_jira_attachments
];
